import { React, useState } from "react";
import moment from "moment";

export default function Form({ dataJSON }) {
  let { view, data } = dataJSON;

  const [employee__name, setEmployeeName] = useState("");
  const [birthday, setBirthday] = useState("");

  //   const onChange = (e) => {
  //     setEmployee__data({ ...employee__data, [e.target.name]: e.target.value });
  //     console.log(employee__data);
  //   };

  const onSubmit = () => {
    console.log("employee__name,birthday", employee__name, birthday);
  };

  const form = Object.entries(view).map(([key, value]) => {
    let { title, children } = value;
    return Object.entries(children).map(([k, val]) => {
      let { component, label, name, path, text } = val;
      return Object.entries(data).map(([data_key, data_value]) => {
        let { date, employee__name } = data_value;
        let employeeName =
          key === data_key && path === "employee__name" ? employee__name : "";

        let birthday = key === data_key && path === "date" ? date : "";
        switch (component) {
          case "Text":
            return (
              <div className="mb-3" key={k}>
                <h4>{title}</h4>
                <input
                  type="text"
                  id={name}
                  name={name}
                  value={employeeName}
                  onChange={(e) => setEmployeeName(e.target.value)}
                  className="form-control"
                />
                <label htmlFor={name} className="form-label">
                  {label}
                </label>
              </div>
            );
          case "Date":
            return (
              <div className="mb-3" key={k}>
                <input
                  type="date"
                  id={name}
                  name={name}
                  value={moment(birthday).format("YYYY-MM-DD")}
                  onChange={(e) => setBirthday(e.target.value)}
                  className="form-control"
                />
                <label htmlFor={name} className="form-label">
                  {label}
                </label>
              </div>
            );
          case "Command":
            return (
              <div key={k}>
                <button
                  type="submit"
                  className="btn btn-primary"
                  onSubmit={onSubmit}
                >
                  {text}
                </button>
              </div>
            );
          default:
            return (
              <div key={0}>
                <span className="red-text">Invalid Field</span>
              </div>
            );
        }
      });
    });
  });

  return <form>{form}</form>;
}
