import React from "react";
import moment from "moment";

export default function Form({ dataJSON }) {
  let { view, data } = dataJSON;

  const onChange = (event) => {
    console.log(event.target.value);
  };

  const form = Object.entries(view).map(([key, value]) => {
    let { title, children } = value;
    return Object.entries(children).map(([k, val]) => {
      let { component, label, name, path, text } = val;
      return Object.entries(data).map(([datakey, datavalue]) => {
        let { date, employee__name } = datavalue;
        let employeeName =
          key === datakey && path === "employee__name" ? employee__name : "";

        let birthday = key === datakey && path === "date" ? date : "";
        switch (component) {
          case "Text":
            return (
              <div className="mb-3" key={k}>
                <h4>{title}</h4>
                <input
                  type="text"
                  id={name}
                  name={name}
                  defaultValue={employeeName}
                  onChange={onChange}
                  className="form-control"
                />
                <label htmlFor={name} className="form-label">
                  {label}
                </label>
              </div>
            );
          case "Date":
            return (
              <div className="mb-3" key={k}>
                <input
                  type="date"
                  id={name}
                  name={name}
                  defaultValue={moment(birthday).format("YYYY-MM-DD")}
                  onChange={onChange}
                  className="form-control"
                />
                <label htmlFor={name} className="form-label">
                  {label}
                </label>
              </div>
            );
          case "Command":
            return (
              <div key={k}>
                <button type="submit" className="btn btn-primary">
                  {text}
                </button>
              </div>
            );
          default:
            return (
              <div key={0}>
                <span className="red-text">Invalid Field</span>
              </div>
            );
        }
      });
    });
  });

  return <form>{form}</form>;
}
