import { React, useState } from "react";
import moment from "moment";

export default function Form({ dataJSON }) {
  let { view, data } = dataJSON;
  const [full_name, setName] = useState("");

  const onChange = (event) => {
    const value = event.target.value;
    setName(value);
  };

  const first = Object.entries(view).map(([key, value]) => {
    let { title, children } = value;

    return Object.entries(children).map(([k, val]) => {
      let { component, label, name, path, text } = val;
      return Object.entries(data).map(([datakey, datavalue]) => {
        let { date, employee__name } = datavalue;
        let employeeName =
          key === datakey && path === "employee__name" ? employee__name : "";

        let birthday = key === datakey && path === "date" ? date : "";

        //console.log("date", date);
        switch (component) {
          case "Text":
            return (
              <div className="mb-3" key={k}>
                <h4>{title}</h4>
                <input
                  type="text"
                  id={name}
                  name={name}
                  defaultValue={employeeName}
                  onChange={onChange}
                  className="form-control"
                />
                <label htmlFor={name} className="form-label">
                  {label}
                </label>
              </div>
            );
          case "Date":
            return (
              <div className="mb-3" key={k}>
                <input
                  type="date"
                  id={name}
                  name={name}
                  value={moment(birthday).format("YYYY-MM-DD")}
                  onChange={onChange}
                  className="form-control"
                />
                <label htmlFor={name} className="form-label">
                  {label}
                </label>
              </div>
            );
          case "Command":
            return (
              <div key={k}>
                <button type="submit" className="btn btn-primary">
                  {text}
                </button>
              </div>
            );
          default:
            return (
              <div key={0}>
                <span className="red-text">Invalid Field</span>
              </div>
            );
        }
      });
    });
  });

  const handleChange = (event) => {
    console.log(event.target.value);
  };
  //Object.values(Object.values(dataJSON.view)[0].children)

  //   const objectValues = Object.values(view);
  //   const objectValuesData = Object.values(data);
  //   const formFields = Object.values(objectValues.map((x) => x.children));
  //   const title = objectValues.map((x) => x.title);

  //console.log("first", first);
  //console.log("json", dataJSON);
  //console.log("view", view);
  //   console.log("data", data);
  //console.log("formFields", formFields);
  const renderFields = (formFields) => {
    return formFields.map((fields) => {
      return Object.values(fields).map((field) => {
        let { component, label, name, path, text } = field;

        switch (component) {
          case "Text":
            return (
              <div className="mb-3" key={component}>
                <input
                  type="text"
                  id={name}
                  name={name}
                  value={"John Smith"}
                  className="form-control"
                  onChange={handleChange}
                />
                <label htmlFor={name} className="form-label">
                  {label}
                </label>
              </div>
            );
          case "Date":
            return (
              <div className="mb-3" key={component}>
                <input
                  type="date"
                  id={name}
                  name={name}
                  value={moment("1971-01-01T00:00:00.0000000").format(
                    "YYYY-MM-DD"
                  )}
                  onChange={handleChange}
                  className="form-control"
                />
                <label htmlFor={name} className="form-label">
                  {label}
                </label>
              </div>
            );
          case "Command":
            return (
              <div key={component}>
                <button type="submit" className="btn btn-primary">
                  {text}
                </button>
              </div>
            );
          default:
            return (
              <div key={component}>
                <span className="red-text">Invalid Field</span>
              </div>
            );
        }
      });
    });
  };
  return <form>{first}</form>;
}
